import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ForecastComponent.css'; // Supondo que você crie este arquivo para estilização

const CryptoForecast = ({ cryptoName, melhoresDias, previsaoDias }) => (
    <div className="crypto-forecast">
        <h2>Previsões de {cryptoName}</h2>
        <h3>Melhores Dias</h3>
        {melhoresDias.map((dia, index) => (
            <div key={index} className="forecast-card">
                <p><strong>Preço Previsto:</strong> {dia.forecast_price.toFixed(2)}</p>
                <p><strong>Volatilidade Prevista:</strong> {dia.forecast_volatility.toFixed(4)}</p>
                <p><strong>Sinal de Compra:</strong> {dia.buy_signal ? 'Sim' : 'Não'}</p>
            </div>
        ))}

        <h3>Previsão Próximos Dias</h3>
        {previsaoDias.map((dia, index) => (
            <div key={index} className="forecast-card">
                <p><strong>Preço Previsto:</strong> {dia.forecast_price.toFixed(2)}</p>
                <p><strong>Volatilidade Prevista:</strong> {dia.forecast_volatility.toFixed(4)}</p>
            </div>
        ))}
    </div>
);

const ForecastComponent = () => {
    const [forecastData, setForecastData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8000/executarmodelo');
                setForecastData(response.data.dates);
            } catch (error) {
                setError("Erro ao buscar os dados. Por favor, tente novamente mais tarde.");
                console.error("Erro ao buscar os dados: ", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    if (!forecastData) {
        return <div>No data available</div>;
    }

    return (
        <div>
            <CryptoForecast 
                cryptoName="Bitcoin" 
                melhoresDias={forecastData.bitcoin.melhores_dias} 
                previsaoDias={forecastData.bitcoin.previsao_dias} 
            />
            <CryptoForecast 
                cryptoName="Ethereum" 
                melhoresDias={forecastData.ethereum.melhores_dias} 
                previsaoDias={forecastData.ethereum.previsao_dias} 
            />
        </div>
    );
};

export default ForecastComponent;
