import React, { useState, useEffect } from 'react';
import CryptoSelector from './CryptoSelector';
import Graph from './Graph';
import Forecast from './Forecast';
import Loading from './Loading';

const Dashboard = () => {
    const [selectedCrypto, setSelectedCrypto] = useState('bitcoin');
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const getData = async () => {
            setLoading(true);
            try {
                // Fazendo a chamada para a API que retorna as previsões
                const response = await fetch(`http://localhost:8000/executarmodelo?crypto=${selectedCrypto}`); // Ajuste a URL conforme necessário
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const result = await response.json();
                setData(result.resultado); // Certifique-se de acessar os dados corretamente
            } catch (error) {
                console.error("Erro ao buscar dados da API", error);
            } finally {
                setLoading(false);
            }
        };

        getData();
    }, [selectedCrypto]);

    if (loading) return <Loading />;

    if (!data || !data.melhores_dias || !data.previsao_dias) {
        return <div>No data available</div>;
    }

    return (
        <div className="container">
            <CryptoSelector selectedCrypto={selectedCrypto} setSelectedCrypto={setSelectedCrypto} />
            <div className="row">
                <div className="col-lg-6">
                    <Graph forecast={data} /> {/* Passando o data diretamente como forecast */}
                </div>
                <div className="col-lg-6">
                    <Forecast forecast={data} /> {/* Passando o data diretamente como forecast */}
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
