import React from 'react';
import { Line, Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const Graph = ({ data }) => {
    if (!data || !data.holt_winters_forecast || !data.previsao_dias) {
        return <div>No data available</div>;
    }

    // Gráfico de Linha para Previsão de Preço (Holt-Winters)
    const priceForecastData = {
        labels: data.holt_winters_forecast.map((_, index) => `Dia ${index + 1}`),
        datasets: [
            {
                label: 'Previsão de Preço (Holt-Winters)',
                data: data.holt_winters_forecast,
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                fill: true,
            },
        ],
    };

    // Gráfico de Linha para Volatilidade Prevista
    const volatilityForecastData = {
        labels: data.previsao_dias.map((_, index) => `Dia ${index + 1}`),
        datasets: [
            {
                label: 'Volatilidade Prevista',
                data: data.previsao_dias.map((dia) => dia.forecast_volatility),
                borderColor: 'rgba(255, 99, 132, 1)',
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
                fill: true,
            },
        ],
    };

    // Gráfico de Barras para Melhores Dias (Preço e Volatilidade)
    const bestDaysData = {
        labels: data.melhores_dias.map((_, index) => `Melhor Dia ${index + 1}`),
        datasets: [
            {
                label: 'Preço Previsto',
                data: data.melhores_dias.map((dia) => dia.forecast_price),
                backgroundColor: 'rgba(54, 162, 235, 0.6)',
            },
            {
                label: 'Volatilidade Prevista',
                data: data.melhores_dias.map((dia) => dia.forecast_volatility),
                backgroundColor: 'rgba(255, 206, 86, 0.6)',
            },
        ],
    };

    return (
        <div className="container mt-4">
            <div className="card mb-4">
                <div className="card-body">
                    <h5 className="card-title">Gráfico de Previsão de Preço (Holt-Winters)</h5>
                    <Line data={priceForecastData} />
                </div>
            </div>
            <div className="card mb-4">
                <div className="card-body">
                    <h5 className="card-title">Gráfico de Volatilidade Prevista</h5>
                    <Line data={volatilityForecastData} />
                </div>
            </div>
            <div className="card mb-4">
                <div className="card-body">
                    <h5 className="card-title">Comparação dos Melhores Dias (Preço e Volatilidade)</h5>
                    <Bar data={bestDaysData} />
                </div>
            </div>
        </div>
    );
};

export default Graph;
