import React from 'react';

const CryptoSelector = ({ selectedCrypto, setSelectedCrypto }) => {
    const cryptocurrencies = [
        { name: 'bitcoin', label: 'Bitcoin', iconClass: 'fab fa-bitcoin' },
        { name: 'ethereum', label: 'Ethereum', iconClass: 'fab fa-ethereum' },
        // Adicione mais criptomoedas aqui, se necessário
    ];

    return (
        <div className="text-center my-4">
            <h2 className="mb-4">Selecione a Criptomoeda</h2>
            <div>
                {cryptocurrencies.map((crypto) => (
                    <button 
                        key={crypto.name}
                        className={`btn btn-outline-${crypto.name === 'bitcoin' ? 'primary' : 'success'} mx-2 ${selectedCrypto === crypto.name ? 'active' : ''}`} 
                        onClick={() => setSelectedCrypto(crypto.name)}
                        aria-pressed={selectedCrypto === crypto.name} // Acessibilidade
                    >
                        <i className={crypto.iconClass}></i> {crypto.label}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default CryptoSelector;
